    {
        "id": "id_46aaa32c_b998_4a14_879b_b2cfbee9ab37",
        "type": "paragraph",
        "content": {
            "de": "<p>{{patient_anrede}} {{patient_name}}<br />{{patient_anschrift}}<br />Geburtsdatum: {{patient_geburtsdatum}}</p>"
        },
